#include <Servo.h>

const int potar = 0;
Servo monServo;

int tring = 3;
int echo = 2;
long echo_read;
long cm;

void setup() {
  pinMode(tring, OUTPUT);
  pinMode(echo, INPUT);

  Serial.begin(9600);
  monServo.attach(9, 750, 2200);

  Serial.println("Commandes : 'h' pour capteur, 's' pour servo, 'x' pour stopper");
}

void loop() {
  if (Serial.available() > 0) {
    char touche = Serial.read();

    if (touche == 'h') {
      capteur();
    }

    if (touche == 's') {
      Serial.println("Mode SERVO active. Tapez 'x' pour l'arreter.");

      // La boucle continue tant qu'on ne re√ßoit pas 'x'
      bool continuer = true;
      while (continuer) {
        servo(); // On ex√©cute la fonction servo

        // On v√©rifie √† CHAQUE passage si une touche est press√©e
        if (Serial.available() > 0) {
          char reset = Serial.read();
          if (reset == 'x') { // On utilise 'x' pour stopper (plus simple qu'un mot complet)
            Serial.println("Arret : Retour au menu principal");
            continuer = false; 
          }
        }
      }
    }
  }
}

void capteur() {
  digitalWrite(tring, LOW);
  delayMicroseconds(2);
  digitalWrite(tring, HIGH);
  delayMicroseconds(10);
  digitalWrite(tring, LOW);

  echo_read = pulseIn(echo, HIGH);
  cm = echo_read / 58;

  Serial.print("Distance en cm : ");
  Serial.println(cm);
}

void servo() {
  int val = analogRead(potar);
  int angle = map(val, 0, 1023, 0, 180);
  monServo.write(angle);
  Serial.print("Angle Servo : ");
  Serial.println(angle);
  delay(100); // Petit d√©lai pour la stabilit√©
}

